class labyrinth {
protected:
    int x_dim{};
    int y_dim{};
    int size{};
    std::vector <std::vector <int>> walls;
    std::vector <std::shared_ptr<key>> keys;

public:
    labyrinth( int x_dim, int y_dim, int size);
    void make_walls();
    int can_pass(int x, int y, char c);
    std::vector <std::vector <int>> get_vector() { return walls; }
    std::vector <std::shared_ptr<key>> get_keys() { return keys; }
    void add_key(int x, int y);
};

labyrinth::labyrinth(int x_d, int y_d, int si) {
    x_dim = x_d;
    y_dim = y_d;
    size = si;
    add_key(319, 188);
    add_key(750, 548);
    add_key(1133, 424);
    add_key(795, 162);
    add_key(531, 526);
}

