int labyrinth::can_pass(int x, int y, char c) {
    int y_tile{ (y - y_dim - ((y - y_dim) % size)) / size };
    int x_tile{ (x - x_dim - ((x - x_dim) % size)) / size };

    if (c == 'l') {
        if (walls[y_tile][x_tile - 1] == 1)
            return 0;
        else
            return 1;
    }
    if (c == 'd') {
        if (walls[y_tile + 1][x_tile] == 1)
            return 0;
        else
            return 1;
    }
    if (c == 'r') {
        if (walls[y_tile][x_tile + 1] == 1)
            return 0;
        else
            return 1;
    }
    if (c == 'u') {
        if (walls[y_tile - 1][x_tile] == 1)
            return 0;
        else
            return 1;
    }
}

void labyrinth::add_key(int x, int y) {
    std::shared_ptr<key> k(new key(x, y));
    keys.push_back(k);
}
